import React, { useState } from 'react';

import './personInfo.component.scss'

export default function PersonInfo(props) {
    const { activeCard } = props;
    console.log(activeCard);

    return (
        <div className='info-container'>
          {activeCard }  <div className='user-img'>
                <img
                    src={activeCard && activeCard.picture.large}
                    alt='user img'
                    className='person-img'
                />
            </div>
            <div className='user-info'>
                <h1 className='user-name'>{activeCard && activeCard.name.title} {activeCard && activeCard.name.first} {activeCard && activeCard.name.last}</h1>
                <p className='user-address'>
                    {activeCard && activeCard.location.street.number}
                    {activeCard && activeCard.location.street.name}
                    {activeCard && activeCard.location.city}
                    {activeCard && activeCard.location.country}
                    {activeCard && activeCard.location.postcode}
                    {activeCard && activeCard.location.state}
                    {activeCard && activeCard.location.offset}
                    {activeCard && activeCard.location.description}</p>
                <p className='user-gender'>{activeCard && activeCard.gender}</p>
            </div>
        </div>
    )
}
